mapper.select:
select c.id, c.name, c.kind, c.type, c.url, c.category_id as categoryId, c.created_at as createdAt, c.updated_at as updatedAt
from courseware c
where c.id != null

    and c.name like  concat('%',#{name},'%')


    and c.kind = #{kind}


    and c.type = #{type}

select case when count(*) = 1 then true else false end
from section_courseware sc
where sc.courseware_id=#{id};

mapper.insert:
insert into section_courseware (relative_id, courseware_id, type)
values
(#{relativeId}, #{coursewareId}, #{type});
