mapper.select:
select qb.id, qb.type, qb.content, qb.choice, qb.answer, qb.pic_url as picUrl, qb.model_id as modelId, qb.category_id as categoryId, qb.created_at as createdAt, qb.updated_at as updatedAt from question_back qb where qb.id is not null
and qb.content like concat('%',#{content},'%')
and qb.type = #{type}
and qb.category_id = #{categoryId}

mapper.update:
update question_back
set type = #{type}, content = #{content}, choice = #{choice}, answer = #{answer}, pic_url = #{picUrl},
model_id = #{modelId}, category_id = #{categoryId}
where id=#{id}::uuid;

mapper.insert:
insert into question_back (type, content, choice, answer, pic_url, model_id, category_id)
values
(#{type}, #{content}, #{choice}, #{answer}, #{picUrl}, #{modelId}, #{categoryId});

insert into QuestionBackAssignment (assignment_id, question_id, score)
values
(#{assignmentId}, #{questionId}, #{score});
